import React from 'react';
import './Input.css';



const input = (props)=> {

    let inputElement= null;
    var inputclasses =["InputElement"];

    if(props.invalid && props.touched){

        const index =inputclasses.indexOf('pop');
        if(index>-1) inputclasses.splice(index,1);

        inputclasses.push("Invalid");

    }

    else if(props.touched){   
        const index =inputclasses.indexOf('pop');
        if(index>-1) inputclasses.splice(index,1);
          inputclasses.push("Valid"); }

    const error = props.errors;


    inputElement = <input 
    type={props.type}
    placeholder={props.placeholder}
    className={inputclasses.join(' ')}
    value={props.value}
    onChange={props.changed}/>


    return(
            <div className="Input">

            {inputElement}

            if(props.)
            <p className="text-danger error-msg">{error}</p> 
            </div>
    );
}

export default input;
