import React, {Component} from 'react';
import './Login.css';
import Input from '../../../components/UI/Input/Input';
import MainPage from '../../../components/UI/MainPage/MainPage';
import Google_logo from '../../../components/UI/Logo/google';
import axios from '../../../axios-shelp/axios-shelp';

class Login extends Component {

    state = {
        Form: {
                name: {
                    elementType:'input',
                    elementConfig: {
                        type:'text',
                        placeholder: 'First Name'
                    },

                value: '',

                validation: {
                    required: true,
                    minLength:5,
                    maxLength:8
                },
                valid: false
            },

            email: {
                elementType:'input',
                elementConfig: {
                    type:'email',
                    placeholder: 'Enter Email address'
                },

            value: '',

            validation: {
                required: true,
                minLength:5,
                maxLength:8
            },
            valid: false
        },


            password: {
                elementType:'input',
                elementConfig: {
                    type:'password',
                    placeholder: 'Password'
                },

            value: '',

            validation: {
                required: true,
                minLength:5,
                maxLength:8
            },
            valid: false
        },


        }
    }





    formHandler = (event)=> {
        event.preventDefault();

        const formData ={};
        const headers = {'Content-Type': 'application/json'};

        for(let formElement in this.state.Form){
            formData[formElement]=this.state.Form[formElement].value; }




        console.log(formData);

        form

        axios.put('/signup',formData)

        .then(response=>{console.log(formData)})

        .catch(error=>{console.log(error)});

    }




//   runs whenever there is any change in the input field
    inputchangeHandler = (event,inputIdentifier)=> {
        const updatedForm = {
            ...this.state.Form
        }
        const updatedElement = {...updatedForm[inputIdentifier]}

        updatedElement.value = event.target.value;
        // validitycheck
        updatedForm[inputIdentifier] = updatedElement;
        this.setState({Form: updatedForm});

    }


    render() {

        const formElementsArray =[];
        for(let key in this.state.Form ){
            formElementsArray.push({
                id:key,
                config:this.state.Form[key]
            });

        };

        let form = (
          <div className="login-form">
              <button className="google-btn"> <Google_logo/>  Continue using google</button>
              <p className="devider-or">OR</p>
            <form >

                {
                    formElementsArray.map(x=> (

                      <Input 
                        key={x.id}
                        elementType={x.config.elementType}
                        elementConfig={x.config.elementConfig}
                        value={x.config.value}
                       //invalid={!x.config.valid}
                        changed={(event)=> this.inputchangeHandler(event,x.id)}/>
                    ))
                }
                <button className="Sumbit-btn" onClick={this.formHandler}>Create account</button>
                <p className="account-login"> Already have an account? <a href="/">Login</a></p>
                 <hr/>

                 <p class="Link-teach">Teach on S-help</p>          
            </form> 
            </div>
        );

        return (
            <div className="SideContent">

                <MainPage 
                heading1={"Start your"}
                heading2={"learning with"}/>

                    {form}
            </div>
        );
    }

}

export default Login;
