import React, {Component} from 'react';
import './Login.css';
import Input from '../../../components/UI/Input/Input';
import MainPage from '../../../components/UI/MainPage/MainPage';


class Login extends Component {

    state = {
        Form: {
            name: {
                elementType:'input',
                elementConfig: {
                    type:'text',
                    placeholder: 'Your First Name'
                },

            value: '',
            validation: {
                required: true,
                minLength:5,
                maxLength:8
            },
            valid: false
        },
          //first input field 






        }
    }


//   runs whenever there is any change in the input field
    inputchangeHandler = (event,inputIdentifier)=> {
        const updatedForm = {
            ...this.state.Form
        }
        const updatedElement = {...updatedForm[inputIdentifier]}

        updatedElement.valid = event.target.value;
        // validitycheck
        updatedForm[inputIdentifier] = updatedElement;
        this.setState({LoginForm: updatedForm});

    }


    render() {

        const formElementsArray =[];
        for(let key in this.state.orderForm ){
            formElementsArray.push({
                id:key,
                config:this.state.orderForm[key]
            });

        };

        let form = (

            <form>

                {
                    formElementsArray.map(x=> (

                      <Input 
                        key={x.id}
                        elementType={x.config.elementType}
                        elementConfig={x.config.elementConfig}
                        value={x.config.value}
                        invalid={!x.config.valid}
                        changed={(event)=> this.inputchangeHandler(event,x.id)}/>
                    ))
                }
                <button onClick={this.loginHandler}> SUMBIT</button>

                {/* <Button btnType="Success" clicked= {this.orderHandler}>ORDER</Button> */}

            </form> 
        );

        return (
            <div className="SideContent">
                <MainPage/>
                    {form}
            </div>
        );
    }

}

export default Login;
